// Yardımcı fonksiyonlar
export const getProductsByCategory = (categorySlug: string): Product[] => {
  return products.filter(p => p.category === categorySlug);
};

export const getProductBySlug = (slug: string): Product | undefined => {
  return products.find(p => p.slug === slug);
};

export const getCategoryBySlug = (slug: string): Category | undefined => {
  return categories.find(c => c.slug === slug);
};

export const searchProducts = (query: string): Product[] => {
  const lowerQuery = query.toLowerCase();
  return products.filter(p => 
    p.name.toLowerCase().includes(lowerQuery) ||
    p.description.toLowerCase().includes(lowerQuery) ||
    (p.tags && p.tags.some(tag => tag.toLowerCase().includes(lowerQuery)))
  );
};

export const getFeaturedProducts = (count: number = 8): Product[] => {
  return products.sort((a, b) => (b.rating || 0) - (a.rating || 0)).slice(0, count);
};

export const getDiscountedProducts = (count: number = 8): Product[] => {
  return products.filter(p => p.discount > 0).sort((a, b) => b.discount - a.discount).slice(0, count);
};

